import { colors, fonts } from "@/theme";
import React, { useState } from "react";
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  TextInput,
  ScrollView,
} from "react-native";
import Slider from "@react-native-community/slider";
import RangeSlider from "react-native-range-slider-expo/src/RangeSlider";
import AnimatedTextInput from "../input/InputText";
import { RouteProp, useRoute } from "@react-navigation/native";
import { StackParamList } from "@/navigator";

type ProjectFilterProps = {
  setModalVisible: (visible: boolean) => void;
};
type ProjectFilterScreenRouteProp = RouteProp<StackParamList, "ProjectFilter">;
const ProjectFilter = () => {
  const route = useRoute<ProjectFilterScreenRouteProp>();
  const { setModalVisible } = route.params;
  const [minValue, setMinValue] = useState(100000); // Minimum price
  const [maxValue, setMaxValue] = useState(500000);

  const handleMinInputChange = (value: string) => {
    const numericValue = parseInt(value, 10);
    if (!isNaN(numericValue) && numericValue < maxValue) {
      setMinValue(numericValue);
    }
  };

  const handleMaxInputChange = (value: string) => {
    const numericValue = parseInt(value, 10);
    if (!isNaN(numericValue) && numericValue > minValue) {
      setMaxValue(numericValue);
    }
  };

  const [selectedOptions, setSelectedOptions] = useState({
    Bedrooms: "All",
    Bathrooms: "All",
    "Square Footage": "Any",
  });

  const handlePress = (filter: string, option: string) => {
    setSelectedOptions((prevState) => ({
      ...prevState,
      [filter]: option,
    }));
  };

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <TouchableOpacity onPress={() => setModalVisible(false)}>
          <Text style={styles.headerButton}>Close</Text>
        </TouchableOpacity>
        <Text style={styles.headerTitle}>Filter Allocated Units</Text>
        <TouchableOpacity>
          <Text style={styles.headerButton}>Reset</Text>
        </TouchableOpacity>
      </View>
      <ScrollView>
        <View style={{ padding: 16 }}>
          <Text style={styles.sectionTitle}>Unit Status</Text>
        </View>

        <View style={styles.row}>
          {["Allocation", "Pending", "Conditional"].map((status) => (
            <TouchableOpacity key={status} style={styles.statusButton}>
              <Text style={styles.statusButtonText}>{status}</Text>
            </TouchableOpacity>
          ))}
        </View>

        <View style={{ paddingVertical: 14, paddingHorizontal: 16 }}>
          <Text style={styles.sectionTitle}>Price</Text>
        </View>
        <View style={{ paddingHorizontal: 18, paddingBottom: 14 }}>
          <RangeSlider
            min={100000} // Minimum value of the range slider
            max={1000000} // Maximum value of the range slider
            step={10000} // Step increment for the slider
            initialFromValue={minValue} // Initial value for the min slider
            initialToValue={maxValue} // Initial value for the max slider
            fromValueOnChange={(fromValue) => setMinValue(fromValue)} // Update min value on change
            toValueOnChange={(toValue) => setMaxValue(toValue)} // Update max value on change
            inRangeBarColor={colors.primary}
            outOfRangeBarColor={colors.iconbackground}
            fromKnobColor={colors.white}
            toKnobColor={colors.white}
          />
        </View>

        <View style={styles.row}>
          <View style={styles.inputGroupHalf}>
            <AnimatedTextInput
              label="Min Price"
              value={minValue.toString()}
              onChangeText={handleMinInputChange}
            />
          </View>

          <View style={styles.inputGroupHalf}>
            <AnimatedTextInput
              label="Max Price"
              value={maxValue.toString()}
              onChangeText={handleMaxInputChange}
              keyboardType="numeric"
            />
          </View>
        </View>

        {/* Filters */}
        <View>
          {/* Bedrooms, Bathrooms, Square Footage */}
          {["Bedrooms", "Bathrooms", "Square Footage"].map((filter) => (
            <View style={{ paddingHorizontal: 8 }} key={filter}>
              <View style={{ paddingVertical: 14, paddingHorizontal: 16 }}>
                <Text style={styles.sectionTitle}>{filter}</Text>
              </View>
              <View style={styles.row}>
                {/* Conditional Mapping for Square Footage */}
                {(filter === "Square Footage"
                  ? ["Any", "500 - 1000", "1001 - 1500", "1501 - 2000", "2001+"]
                  : ["All", "1+", "2+", "3+"]
                ).map((option) => (
                  <TouchableOpacity
                    key={option}
                    style={[
                      styles.optionButton,
                      selectedOptions[filter] === option &&
                        styles.selectedButton, // Highlight selected button
                    ]}
                    onPress={() => handlePress(filter, option)}
                  >
                    <Text
                      style={[
                        styles.statusButtonText,
                        selectedOptions[filter] === option &&
                          styles.selectedButtonText, // Highlight selected text
                      ]}
                    >
                      {option}
                    </Text>
                  </TouchableOpacity>
                ))}
              </View>
            </View>
          ))}
        </View>
      </ScrollView>
      <View style={{ padding: 16, backgroundColor: colors.white }}>
        <TouchableOpacity style={styles.applyButton}>
          <Text style={styles.applyButtonText}>Apply</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    // padding: 16,
    backgroundColor: colors.formBackground,
  },
  header: {
    paddingTop: 20,
    paddingHorizontal: 18,
    backgroundColor: colors.white,
    height: 60,
    flexDirection: "row",
    justifyContent: "space-between",
    // marginBottom: 16,
    marginTop: 0,
  },
  headerButton: {
    fontSize: 16,
    fontWeight: 400,
    lineHeight: 24,
    color: colors.primary,
    fontFamily: fonts.calibri.regular,
  },
  headerTitle: {
    fontSize: 16,
    fontWeight: 700,
    lineHeight: 24,
    color: colors.black,
    fontFamily: fonts.calibri.bold,
  },
  sectionTitle: {
    fontSize: 20,
    fontWeight: 700,
    lineHeight: 24,
    color: colors.black,
    fontFamily: fonts.calibri.bold,
  },
  row: {
    flexDirection: "row",
    marginBottom: 8,
    flexWrap: "wrap",
    gap: 8,
    paddingHorizontal: 10,
  },
  statusButton: {
    paddingVertical: 10,
    paddingHorizontal: 12,
    borderRadius: 100,
    borderColor: colors.disablebutton,
    borderWidth: 1,
    marginRight: 8,
    backgroundColor: colors.white,
  },

  statusButtonText: {
    fontSize: 16,
    fontWeight: 400,
    lineHeight: 24,
    color: colors.black,
    fontFamily: fonts.calibri.regular,
  },

  selectedButton: {
    backgroundColor: colors.allocted, // Blue background for selected button
  },

  selectedButtonText: {
    color: colors.primary, // White text for selected button
  },

  slider: {
    height: 10, // Adjust the height of the slider track
    width: "100%", // Set the width of the slider to fill its container
  },
  track: {
    backgroundColor: "white", // Set the background color of the track to white
    height: 10, // Track height
    borderRadius: 5, // Round the edges of the track
  },
  selectedTrack: {
    backgroundColor: colors.primary, // Use the primary color for the selected part of the track
    height: 10,
    borderRadius: 5,
  },
  handle: {
    backgroundColor: "white", // Set the color of the dots (handles) to white
    borderColor: colors.primary, // Optionally set the border color of the dots to the primary color
    borderWidth: 2, // Optional border width for the dots
    width: 20, // Size of the dots (handles)
    height: 20,
    borderRadius: 10, // Make the dots circular
  },
  rangeLabels: {
    flexDirection: "row",
    justifyContent: "space-between",
    marginTop: 8,
  },
  rangeLabel: {
    fontSize: 16,
    color: colors.black,
    fontFamily: fonts.calibri.regular,
  },
  priceInputs: {
    flexDirection: "row",
    justifyContent: "space-between",
    paddingHorizontal: 18,
    marginBottom: 14,
  },
  inputGroupHalf: {
    position: "relative",
    flex: 1,
    marginHorizontal: 5,
  },
  priceInput: {
    flex: 1,
    borderColor: "#CCC",
    borderWidth: 1,
    borderRadius: 8,
    padding: 8,
    marginRight: 8,
  },
  optionButton: {
    paddingVertical: 10,
    paddingHorizontal: 16,
    borderRadius: 80,
    borderColor: colors.disablebutton,
    borderWidth: 1,
    marginRight: 8,
    backgroundColor: colors.white,
  },
  applyButton: {
    backgroundColor: colors.primary,
    padding: 16,
    alignItems: "center",
    borderRadius: 8,
    marginTop: 16,
  },
  applyButtonText: {
    color: "#FFF",
    fontWeight: "bold",
  },
});

export default ProjectFilter;
