import React from "react";
import { View, Text, TouchableOpacity, StyleSheet } from "react-native";
import { MaterialIcons } from "@expo/vector-icons";
import { colors, fonts } from "@/theme";
import { useNavigation } from "@react-navigation/native";
import { useDispatch } from "react-redux";
import { setStep } from "@/redux/slices/stepSlice";
import { setIsChecked } from "@/redux/slices/authSlice";
import Button from "@/components/Button";

const PasswordUpdateSuccess: React.FC = () => {
  const navigation = useNavigation();
  const dispatch = useDispatch();

  const handleContinue = () => {
    dispatch(setStep(1));
    navigation.navigate("RegisterScreen");
  };
  return (
    <View style={styles.container}>
      <View style={styles.iconContainer}>
        <View style={styles.circle}>
          <MaterialIcons name="check" size={120} color={colors.IndicatorBar} />
        </View>
      </View>
      <Text style={styles.title}>Password Updated</Text>
      <Text style={styles.message}>
        Your password has been updated successfully Use your new password to log
        in.
      </Text>
      <View style={{ position: "absolute", bottom: 80, width: "100%" }}>
        <Button label="Close" onPress={handleContinue} />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#FFFFFF",
    padding: 20,
  },
  iconContainer: {
    marginBottom: 20,
  },
  circle: {
    width: 150,
    height: 150,
    backgroundColor: colors.success,
    borderRadius: "50%",
    justifyContent: "center",
    alignItems: "center",
  },
  title: {
    fontSize: 24,
    fontWeight: 700,
    fontFamily: fonts.calibri.regular,
    marginBottom: 20,
    textAlign: "center",
    color: "#000",
  },
  message: {
    fontFamily: fonts.calibri.regular,
    fontSize: 16,
    fontWeight: 400,
    color: colors.gray,
    textAlign: "center",
    marginBottom: 30,
    width: "90%",
  },
});

export default PasswordUpdateSuccess;
