import React, { useEffect, useState } from "react";
import { Text, StyleSheet, View } from "react-native";
import Icon from "react-native-vector-icons/Entypo";
import AlertIcon from "react-native-vector-icons/Ionicons";
import { colors } from "@/theme";
import AnimatedTextInput from "@/components/input/InputText";

type PasswordValidationState = {
  length: boolean;
  noEmailOrName: boolean;
  symbolOrNumber: boolean;
  uppercase: boolean;
};

const AccountDetails: React.FC<{
  formState: {
    email: string;
    password: string;
    confirmPassword: string;
  };
  updateFormState: (
    field: "email" | "password" | "confirmPassword",
    value: string
  ) => void;
  validityState: {
    emailValid: boolean;
    passwordValid: boolean;
    passwordsMatch: boolean;
  };
  updateValidityState: (
    field: "emailValid" | "passwordsMatch" | "passwordValid",
    isValid: boolean
  ) => void;
}> = ({ formState, updateFormState, validityState, updateValidityState }) => {
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [focusedField, setFocusedField] = useState<string | null>(null);
  const [passwordValidation, setPasswordValidation] =
    useState<PasswordValidationState>({
      length: false,
      noEmailOrName: false,
      symbolOrNumber: false,
      uppercase: false,
    });

  const validateEmail = (value: string) => {
    updateFormState("email", value);
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    updateValidityState("emailValid", emailRegex.test(value));
  };

  const validatePassword = (value: string) => {
    updateFormState("password", value);

    setPasswordValidation({
      length: value.length >= 8,
      noEmailOrName: !value.includes(formState.email),
      symbolOrNumber: /[!@#$%^&*(),.?":{}|<>0-9]/.test(value),
      uppercase: /[A-Z]/.test(value),
    });
  };

  useEffect(() => {
    updateValidityState(
      "passwordValid",
      Object.values(passwordValidation).every((value) => value === true)
    );
  }, [passwordValidation]);

  const validateConfirmPassword = (value: string) => {
    updateFormState("confirmPassword", value);
    updateValidityState("passwordsMatch", value === formState.password);
  };

  const calculatePasswordStrength = () => {
    const validCount = Object.values(passwordValidation).filter(Boolean).length;
    if (validCount === 0 || validCount === 1) return "Weak";
    if (validCount === 2 || validCount === 3) return "Moderate";
    return "Strong";
  };

  const passwordStrength = calculatePasswordStrength();

  const togglePasswordVisibility = () => setShowPassword((prev) => !prev);
  const toggleConfirmPasswordVisibility = () =>
    setShowConfirmPassword((prev) => !prev);
  // const handleFocus = (field: string) => setFocusedField(field);
  // const handleBlur = () => setFocusedField(null);

  return (
    <View style={styles.container}>
      <Text style={styles.sectionTitle}>Account Details</Text>
      <AnimatedTextInput
        label={"Email Address"}
        value={formState.email}
        onChangeText={validateEmail}
      />
      {!validityState.emailValid && (
        <View style={{ marginBottom: 14 }}>
          <Text style={styles.errorText}>
            <Icon name="cross" size={16} color="black" /> Enter a valid email
            address
          </Text>
        </View>
      )}
      <View>
        <AnimatedTextInput
          label="Password"
          value={formState.password}
          onChangeText={validatePassword}
          secureTextEntry={!showPassword}
          error={!validityState.passwordValid}
        />
      </View>
      <View>
        <AnimatedTextInput
          label="Confirm Password"
          value={formState.confirmPassword}
          onChangeText={validateConfirmPassword}
          secureTextEntry={!showConfirmPassword}
          error={!validityState.passwordValid}
        />
      </View>
      <View style={styles.validationContainer}>
        {passwordStrength === "Weak" && (
          <View style={styles.passwordStrengthContainer}>
            <AlertIcon name={"alert-circle"} size={16} color={colors.error} />
            <Text style={[styles.passwordStrengthText, styles.weakText]}>
              Password strength: {passwordStrength}
            </Text>
          </View>
        )}

        {Object.entries(passwordValidation).map(([key, isValid]) => (
          <Text key={key} style={[styles.validationText, styles.valid]}>
            <Icon
              name={isValid ? "check" : "cross"}
              size={16}
              color={"black"}
            />
            {key === "length"
              ? "Must be at least 8 characters"
              : key === "noEmailOrName"
                ? "Cannot include your email address"
                : key === "symbolOrNumber"
                  ? "Must have at least one symbol or number"
                  : "Must have at least one uppercase letter"}
          </Text>
        ))}
        {!validityState.passwordsMatch && (
          <Text style={styles.errorText}>
            <Icon name="cross" size={16} color="black" /> Passwords must match
          </Text>
        )}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    paddingVertical: 16,
  },
  sectionTitle: {
    fontSize: 24,
    fontWeight: "700",
    marginBottom: 24,
    color: "#333",
  },
  validationContainer: {
    marginTop: 8,
    marginVertical: 14,
  },
  validationText: {
    fontSize: 14,
    marginVertical: 4,
  },
  valid: {
    color: "black",
  },

  errorText: {
    color: "black",
    marginTop: 4,
    fontSize: 14,
  },
  passwordStrengthContainer: {
    flexDirection: "row",
    alignItems: "center",
    marginTop: 0,
  },
  passwordStrengthText: { marginLeft: 8, fontSize: 14 },
  weakText: { color: colors.error },
});

export default AccountDetails;
