import FileIcon from "@/assets/icons/FileIcon";
import Button from "@/components/Button";
import OptionCard from "@/components/cards/OptionCard";
import WelcomeSection from "@/components/header/WelcomeSection";
import { setUserNull } from "@/redux/slices/authSlice";
import { setStep } from "@/redux/slices/stepSlice";
import { RootState } from "@/redux/store";
import { colors, fonts } from "@/theme";
import { useNavigation } from "@react-navigation/native";
import React from "react";
import {
  View,
  Text,
  StyleSheet,
  Image,
  TouchableOpacity,
  ScrollView,
} from "react-native";
import Swiper from "react-native-swiper";
import Icon from "react-native-vector-icons/Feather";
import Icons from "react-native-vector-icons/Octicons";
import { useDispatch, useSelector } from "react-redux";

const Home = () => {
  const navigation = useNavigation();
  const dispatch = useDispatch();

  const handleLogout = () => {
    dispatch(setStep(1));
    dispatch(setUserNull());
  };

  return (
    <ScrollView style={styles.container}>
      <View>
        <Swiper
          style={styles.swiper}
          showsPagination={false}
          autoplay={true}
          autoplayTimeout={3}
        >
          {[...Array(3)].map((_, index) => (
            <View style={styles.promotionCard} key={index}>
              <Image
                source={{
                  uri: `https://picsum.photos/seed/picsum${index}/200/300`,
                }}
                style={styles.promotionImage}
              />
              <View style={styles.promotionContent}>
                <Text style={styles.promotionTitle}>
                  Summer Promotions {index + 1}
                </Text>
                <Text style={styles.promotionDescription}>
                  Lorem Ipsum has been the industry's standard dummy text ever
                  since the 1500s, when an unknown printer took
                </Text>
                <Text style={styles.validDate}>Valid until Nov 11, 2024</Text>
              </View>
            </View>
          ))}
        </Swiper>
      </View>
      <WelcomeSection title="Welcome David" text="Menkes ID: 15550" />
      <View style={styles.options}>
        <OptionCard
          onPress={() => navigation.navigate("AllocatedUnits")}
          icon={<Icon name="folder" size={22} color={colors.primary} />}
          label="Allocated Units"
        />
        <OptionCard
          onPress={() => navigation.navigate("FirmUnits")}
          icon={<Icons name="check-circle" size={22} color={colors.primary} />}
          label="Firm Units"
          badge="1"
        />
        <OptionCard icon={<FileIcon />} label="Worksheets" />
      </View>

      <View style={{ padding: 16 }}>
        <Button label="Submit Worksheet" icon="plus" onPress={handleLogout} />
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: colors.formBackground,
  },
  swiper: {
    height: 280,
  },
  promotionCard: {
    margin: 16,
    borderRadius: 8,
    position: "relative",
    marginHorizontal: 10,
  },
  promotionImage: {
    width: "100%",
    height: 260,
    borderRadius: 8,
  },
  promotionContent: {
    padding: 16,
    position: "absolute",
    bottom: 0,
    left: 0,
    width: "90%",
  },
  promotionTitle: {
    fontSize: 16,
    fontWeight: 700,
    lineHeight: 24,
    fontFamily: fonts.calibri.bold,
    marginBottom: 4,
    color: colors.white,
  },
  promotionDescription: {
    textAlign: "left",
    fontSize: 14,
    fontWeight: 400,
    lineHeight: 20,
    fontFamily: fonts.calibri.regular,
    // marginBottom: 8,
    color: colors.white,
  },
  validDate: {
    width: "45%",
    backgroundColor: colors.white,
    borderRadius: 50,
    paddingVertical: 5,
    alignItems: "center",
    marginTop: 10,
    textAlign: "center",
    fontSize: 12,
    fontWeight: 400,
    lineHeight: 20,
    fontFamily: fonts.calibri.regular,
  },
  options: {
    paddingHorizontal: 16,
  },
});

export default Home;
