import React, { useState } from "react";
import { Ionicons } from "@expo/vector-icons";
import {
  View,
  Text,
  StyleSheet,
  TextInput,
  FlatList,
  TouchableOpacity,
  Modal,
} from "react-native";
import { colors, fonts } from "@/theme";
import { useNavigation } from "@react-navigation/native";
import ProjectFilter from "../components/filter/ProjectFilter";
import { units } from "@/utils/constant";
import { useDispatch, useSelector } from "react-redux";
import { closeModal } from "@/redux/slices/modalSlice";
import AllocatedRequesCallback from "../components/callBackscreen/AllocatedRequesCallback";
import SearchHeader from "@/components/searchandfilter/SearchHeader";
import Button from "@/components/Button";
import UnitItem from "@/components/tabs/UnitItems";

const ProjectDetails: React.FC = () => {
  const navigation = useNavigation();
  const [modalVisible, setModalVisible] = useState(false);

  const dispatch = useDispatch();
  const isModalOpen = useSelector((state: any) => state.modal.isOpen); // Access modal state

  const handleCloseModal = () => {
    dispatch(closeModal()); // Dispatch action to close the modal
  };

  return (
    <View style={styles.container}>
      <SearchHeader onFilterPress={() => setModalVisible(true)} />

      <View style={styles.header}>
        <Text style={styles.headerText}>3 Allocated Units</Text>
      </View>

      <View
        style={{
          paddingHorizontal: 0,
          justifyContent: "space-between",
          height: "75%",
        }}
      >
        <FlatList
          data={units}
          keyExtractor={(item) => item.id}
          renderItem={({ item }) => <UnitItem item={item} />}
        />
        <View style={{ padding: 16 }}>
          <Button label="Submit Worksheet" icon="plus" />
        </View>
      </View>

      {/* Submit Button */}
      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => setModalVisible(false)}
      >
        <View style={styles.modalOverlay}>
          <ProjectFilter setModalVisible={setModalVisible} />
        </View>
      </Modal>

      <Modal
        visible={isModalOpen}
        transparent={true}
        animationType="slide"
        onRequestClose={handleCloseModal}
      >
        <View style={styles.modalOverlay}>
          <AllocatedRequesCallback />
        </View>
      </Modal>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: colors.formBackground,
  },
  header: {
    marginBottom: 10,
    paddingHorizontal: 20,
  },
  headerText: {
    fontSize: 16,
    fontWeight: 700,
    lineHeight: 24,
    fontFamily: fonts.calibri.bold,
    color: colors.black,
  },
  modalOverlay: {
    marginTop: 40,
    flex: 1,
    justifyContent: "flex-end",
    backgroundColor: "rgba(0, 0, 0, 0.5)",
  },
});

export default ProjectDetails;
