import { colors, fonts } from "@/theme";
import React from "react";
import {
  StyleSheet,
  Text,
  TextStyle,
  TouchableOpacity,
  View,
  ViewStyle,
} from "react-native";
import FeatherIcon from "react-native-vector-icons/Feather";

interface ButtonProps {
  label?: string;
  icon?: string; // Icon name (optional)
  onPress?: () => void; // Optional onPress handler
  disabled?: boolean; // Disabled state (optional)
  buttonStyle?: ViewStyle; // Optional additional styles for the button
  textStyle?: TextStyle; // Optional additional styles for the text
}

const Button: React.FC<ButtonProps> = ({
  label,
  icon,
  onPress,
  disabled,
  buttonStyle,
  textStyle,
}) => {
  return (
    <View style={styles.footerButtonContainer}>
      <TouchableOpacity
        style={[
          styles.footerButton,
          buttonStyle,
          disabled && { backgroundColor: colors.disablebutton },
        ]}
        onPress={onPress}
        disabled={disabled}
      >
        {icon && (
          <FeatherIcon
            name={icon as string}
            size={30}
            color={colors.white}
            style={styles.icon}
          />
        )}
        <Text style={[styles.footerButtonText, textStyle]}>{label}</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  footerButtonContainer: {
    width: "100%",
    // paddingBottom: 12,
  },
  footerButton: {
    backgroundColor: colors.primary,
    padding: 10,
    alignItems: "center",
    borderRadius: 8,
    flexDirection: "row",
    justifyContent: "center",
  },
  footerButtonText: {
    fontSize: 16,
    fontWeight: "700",
    lineHeight: 24,
    fontFamily: fonts.calibri.bold,
    color: colors.white,
  },
  icon: {
    marginRight: 8,
  },
});

export default Button;
