import { colors, fonts } from "@/theme";
import React from "react";
import {
  ScrollView,
  StyleSheet,
  Text,
  TouchableOpacity,
  View,
} from "react-native";
import AnimatedTextInput from "../input/InputText";

type ProjectFilterProps = {
  setModalVisible: (visible: boolean) => void;
};

const FirmFilter = ({ setModalVisible }: ProjectFilterProps) => {
  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <TouchableOpacity onPress={() => setModalVisible(false)}>
          <Text style={styles.headerButton}>Close</Text>
        </TouchableOpacity>
        <Text style={styles.headerTitle}>Filter Firm Units</Text>
        <TouchableOpacity>
          <Text style={styles.headerButton}>Reset</Text>
        </TouchableOpacity>
      </View>
      <ScrollView>
        <View style={{ padding: 16 }}>
          <Text style={styles.sectionTitle}>Post Dated Cheques</Text>
        </View>

        <View style={styles.row}>
          {["InComplete", "Complete"].map((status) => (
            <TouchableOpacity key={status} style={styles.statusButton}>
              <Text style={styles.statusButtonText}>{status}</Text>
            </TouchableOpacity>
          ))}
        </View>

        <View style={{ paddingVertical: 14, paddingHorizontal: 16 }}>
          <Text style={styles.sectionTitle}>Mortgage Pre-Approval Letter</Text>
        </View>

        <View style={styles.row}>
          {["InComplete", "Complete"].map((status) => (
            <TouchableOpacity key={status} style={styles.statusButton}>
              <Text style={styles.statusButtonText}>{status}</Text>
            </TouchableOpacity>
          ))}
        </View>
      </ScrollView>
      <View style={{ padding: 16 }}>
        <TouchableOpacity style={styles.applyButton}>
          <Text style={styles.applyButtonText}>Apply</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    height: "50%",

    backgroundColor: colors.formBackground,
  },
  header: {
    paddingTop: 20,
    paddingHorizontal: 18,
    backgroundColor: colors.white,
    height: 60,
    flexDirection: "row",
    justifyContent: "space-between",
    marginTop: 0,
  },
  headerButton: {
    fontSize: 16,
    fontWeight: 400,
    lineHeight: 24,
    color: colors.primary,
    fontFamily: fonts.calibri.regular,
  },
  headerTitle: {
    fontSize: 16,
    fontWeight: 700,
    lineHeight: 24,
    color: colors.black,
    fontFamily: fonts.calibri.bold,
  },
  sectionTitle: {
    fontSize: 20,
    fontWeight: 700,
    lineHeight: 24,
    color: colors.black,
    fontFamily: fonts.calibri.bold,
  },
  row: {
    flexDirection: "row",
    marginBottom: 8,
    flexWrap: "wrap",
    gap: 8,
    paddingHorizontal: 10,
  },
  statusButton: {
    paddingVertical: 10,
    paddingHorizontal: 12,
    borderRadius: 100,
    borderColor: colors.disablebutton,
    borderWidth: 1,
    marginRight: 8,
    backgroundColor: colors.white,
  },

  statusButtonText: {
    fontSize: 16,
    fontWeight: 400,
    lineHeight: 24,
    color: colors.black,
    fontFamily: fonts.calibri.regular,
  },

  applyButton: {
    backgroundColor: colors.primary,
    padding: 16,
    alignItems: "center",
    borderRadius: 8,
    marginTop: 16,
    marginBottom: 20,
  },
  applyButtonText: {
    color: "#FFF",
    fontWeight: "bold",
  },
});

export default FirmFilter;
