import React, { useState } from "react";
import {
  View,
  Text,
  ScrollView,
  StyleSheet,
  TouchableOpacity,
  Alert,
  ActivityIndicator,
} from "react-native";
import { useDispatch, useSelector } from "react-redux";
import { NavigationProp, useNavigation } from "@react-navigation/native";
import { colors, fonts } from "@/theme";
import { AppDispatch, RootState } from "@/redux/store";
import { registerUser } from "@/redux/slices/authSlice";
import { setStep } from "@/redux/slices/stepSlice";
import Button from "@/components/Button";
import { StackParamList } from "@/navigator";

const TermsConditions: React.FC<{
  formData: any;
  profileImage: string | null;
  handleNext: () => void;
}> = ({ formData, profileImage, handleNext }) => {
  const navigation = useNavigation<NavigationProp<StackParamList>>();
  const dispatch = useDispatch<AppDispatch>();
  const [loading, setLoading] = useState(false);
  const { isCheckedL } = useSelector((state: RootState) => state.registration);

  const handleAgreeAndContinue = async () => {
    setLoading(true);
    try {
      const payload = {
        ...formData,
        profileImage,
      };

      const resultAction = await dispatch(registerUser(payload));

      if (registerUser.fulfilled.match(resultAction)) {
        const successPayload = resultAction.payload as { message: string };
        Alert.alert(
          "Success",
          successPayload.message || "Registration successful!"
        );
        dispatch(setStep(1));
        if (!isCheckedL) {
          navigation.navigate("RegisterSuccess");
        }

        if (handleNext) handleNext();
      } else if (registerUser.rejected.match(resultAction)) {
        const errorPayload = resultAction.payload as string;
        Alert.alert("Error", errorPayload || "Something went wrong!");
      }
    } catch (error) {
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.headerText}>Terms & Conditions</Text>
      </View>

      <ScrollView style={styles.scrollView}>
        <Text style={styles.contentText}>
          Lorem, ipsum dolor sit amet consectetur adipisicing elit. Quos ipsam
          itaque tempore tenetur labore repellat repudiandae deserunt
          consequuntur! Voluptatibus, totam cum inventore sit delectus
          repellendus quisquam necessitatibus iusto qui fuga nostrum veritatis
          ducimus explicabo ipsam quis quia quo numquam molestias. Veritatis
          quisquam quos, incidunt sapiente earum eos consequuntur voluptatum
          consectetur repellat! Repellendus, deleniti culpa ratione distinctio
          explicabo nemo eveniet iusto voluptatum? Numquam modi minima corporis
          earum officiis nisi doloremque ullam officia quibusdam ipsam, laborum
          nemo ducimus velit quaerat dicta amet ipsum corrupti! Vero illum
          minima fuga laborum placeat eaque, dolor animi pariatur deleniti
          aliquam a necessitatibus earum enim labore unde dignissimos nostrum
          aut architecto quia in. Quia perspiciatis molestias sapiente sunt
          culpa quisquam, laborum libero repellendus porro, cumque atque sint
          voluptas necessitatibus? Rerum aliquid et eveniet at cumque. Qui, quis
          officiis, temporibus unde debitis perspiciatis laudantium dolorum
          architecto enim quibusdam sunt dolor vero autem impedit quisquam
          nulla. Corrupti quidem, pariatur cupiditate ex rerum ipsum amet
          placeat porro perferendis a aliquam magni est hic deleniti praesentium
          nemo id, accusantium ad minus. A suscipit impedit inventore mollitia
          similique maxime quas numquam fugiat animi blanditiis. Ab ex sunt am
          cumque minima sunt, ea nulla alias.
        </Text>
        {/* Add more content here */}
      </ScrollView>

      <TouchableOpacity
        style={[styles.button, loading && styles.buttonDisabled]}
        disabled={loading}
      >
        {loading ? (
          <ActivityIndicator color="#FFFFFF" />
        ) : (
          <Button label="Agree and continue" onPress={handleAgreeAndContinue} />
        )}
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: colors.formBackground,
  },
  header: {
    paddingHorizontal: 20,
    paddingVertical: 18,
  },
  headerText: {
    fontSize: 24,
    fontWeight: "700",
    lineHeight: 30,
    color: "#000000",
    fontFamily: fonts.calibri.bold,
  },
  scrollView: {
    flex: 1,
    marginHorizontal: 20,
    marginBottom: 10,
  },
  contentText: {
    fontSize: 16,
    fontWeight: "400",
    fontFamily: fonts.calibri.regular,
    color: colors.gray,
    lineHeight: 24,
    marginBottom: 12,
    textAlign: "left",
  },
  button: {
    backgroundColor: colors.primary,
    paddingVertical: 15,
    alignItems: "center",
    marginHorizontal: 20,
    marginBottom: 20,
    borderRadius: 5,
  },
  buttonDisabled: {
    backgroundColor: colors.gray,
  },
  buttonText: {
    fontSize: 16,
    color: "#FFFFFF",
    fontWeight: "bold",
  },
});

export default TermsConditions;
