import React, { useState } from "react";
import { View, Modal, StyleSheet } from "react-native";
import { useNavigation } from "@react-navigation/native";
import { useDispatch, useSelector } from "react-redux";
import { closeModal } from "@/redux/slices/modalSlice";
import SearchHeader from "@/components/searchandfilter/SearchHeader";
import TabsContainer from "@/components/tabs/TabsContainer";
import Button from "@/components/Button";
import ProjectFilter from "@/components/filter/ProjectFilter";
import AllocatedRequesCallback from "@/components/callBackscreen/AllocatedRequesCallback";

import ProjectList from "./ProjectList";
import { colors } from "@/theme";
import ProjectIcon from "@/assets/icons/ProjectIcon";
import ListIcon from "@/assets/icons/ListIcon";
import UnitList from "@/components/tabs/UnitList";

const AllocatedUnits = () => {
  const navigation = useNavigation();
  const dispatch = useDispatch();
  const isModalOpen = useSelector((state: any) => state.modal.isOpen);

  const [viewMode, setViewMode] = useState("project");
  const [modalVisible, setModalVisible] = useState(false);

  const tabs = [
    { id: "project", label: "Project", icon: <ProjectIcon /> },
    { id: "list", label: "List", icon: <ListIcon /> },
  ];

  const renderComponents = {
    project: (
      <UnitList onUnitPress={() => navigation.navigate("ProjectDetails")} />
    ),
    list: <ProjectList />,
  };

  return (
    <View style={styles.container}>
      <SearchHeader onFilterPress={() => setModalVisible(true)} />

      <TabsContainer
        tabs={tabs}
        viewMode={viewMode}
        setViewMode={setViewMode}
        renderComponents={renderComponents}
      />

      <View style={styles.buttonContainer}>
        <Button label="Submit Worksheet" icon="plus" />
      </View>

      {/* Filter Modal */}
      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => setModalVisible(false)}
      >
        <View style={styles.modalOverlay}>
          <ProjectFilter setModalVisible={setModalVisible} />
        </View>
      </Modal>

      {/* Callback Modal */}
      <Modal
        visible={isModalOpen}
        transparent={true}
        animationType="slide"
        onRequestClose={() => dispatch(closeModal())}
      >
        <View style={styles.modalOverlay}>
          <AllocatedRequesCallback />
        </View>
      </Modal>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: colors.formBackground,
  },
  buttonContainer: {
    padding: 16,
  },
  modalOverlay: {
    flex: 1,
    marginTop: 40,
    justifyContent: "flex-end",
    backgroundColor: "rgba(0, 0, 0, 0.5)",
  },
});

export default AllocatedUnits;
