import React, { useState } from "react";
import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  Image,
  Modal,
  FlatList,
} from "react-native";
import { colors, fonts } from "@/theme";
import { useNavigation } from "@react-navigation/native";
import ProjectFilter from "../components/filter/ProjectFilter";
import { useDispatch, useSelector } from "react-redux";
import { closeModal } from "@/redux/slices/modalSlice";
import AllocatedRequesCallback from "@/components/callBackscreen/AllocatedRequesCallback";
import SearchHeader from "@/components/searchandfilter/SearchHeader";
import TabsContainer from "@/components/tabs/TabsContainer";
import Button from "@/components/Button";
import ProjectList from "@/AllocatedUnits/ProjectList";
import FirmUnitList from "./FirmUnitList";
import FirmFilter from "@/components/filter/FirmFilter";
import ProjectIcon from "@/assets/icons/ProjectIcon";
import ListIcon from "@/assets/icons/ListIcon";
import UnitList from "@/components/tabs/UnitList";

interface Unit {
  id: string;
  name: string;
  type: string;
  location: string;
}

const FirmUnits = () => {
  const navigation = useNavigation();
  const dispatch = useDispatch();
  const isModalOpen = useSelector((state: any) => state.modal.isOpen); // Access modal state

  const handleCloseModal = () => {
    dispatch(closeModal()); // Dispatch action to close the modal
  };

  const [units] = useState<Unit[]>([
    { id: "1", name: "BRAVO", type: "Residential", location: "Toronto, ON" },
    {
      id: "2",
      name: "SUGAR WHARF",
      type: "Residential",
      location: "Toronto, ON",
    },
  ]);

  const [viewMode, setViewMode] = useState("project");
  const [modalVisible, setModalVisible] = useState(false);

  const tabs = [
    { id: "project", label: "Project", icon: <ProjectIcon /> },
    { id: "list", label: "List", icon: <ListIcon /> },
  ];

  const renderComponents = {
    project: (
      <UnitList onUnitPress={() => navigation.navigate("WorkSheetDetails")} />
    ),
    list: <FirmUnitList />,
  };

  return (
    <View style={styles.container}>
      <SearchHeader onFilterPress={() => setModalVisible(true)} />

      <TabsContainer
        tabs={tabs}
        viewMode={viewMode}
        setViewMode={setViewMode}
        renderComponents={renderComponents}
      />
      <View style={{ padding: 16 }}>
        <Button label="Submit Worksheet" icon="plus" />
      </View>
      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => setModalVisible(false)}
      >
        <View style={styles.modalOverlay}>
          <FirmFilter setModalVisible={(value) => setModalVisible(value)} />
        </View>
      </Modal>

      <Modal
        visible={isModalOpen}
        transparent={true}
        animationType="slide"
        onRequestClose={handleCloseModal}
      >
        <View style={styles.modalOverlay}>
          <AllocatedRequesCallback />
        </View>
      </Modal>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: colors.formBackground,
  },
  modalOverlay: {
    marginTop: 40,
    flex: 1,
    justifyContent: "flex-end",
    backgroundColor: "rgba(0, 0, 0, 0.5)",
  },
});

export default FirmUnits;
