import React from "react";
import { TextInput, Text, StyleSheet, View } from "react-native";
import { fonts } from "@/theme";

import Icon from "react-native-vector-icons/Entypo";
import AnimatedTextInput from "@/components/input/InputText";

type BrokerageDetailsProps = {
  formState: {
    recoNumber: string;
    brokerageName: string;
    officePhone: string;
    brokerageAddress: string;
    country: string;
    province: string;
    city: string;
    postalCode: string;
  };
  updateFormState: (
    field: keyof BrokerageDetailsProps["formState"],
    value: string
  ) => void;
};

const BrokerageDetails: React.FC<BrokerageDetailsProps> = ({
  formState,
  updateFormState,
}) => {
  return (
    <View>
      <Text style={styles.sectionTitle}>Brokerage Details</Text>

      <AnimatedTextInput
        label={"Reco #"}
        value={formState.recoNumber}
        onChangeText={(value: string) => updateFormState("recoNumber", value)}
      />

      <AnimatedTextInput
        label={"Brokerage Name"}
        value={formState.brokerageName}
        onChangeText={(value: string) =>
          updateFormState("brokerageName", value)
        }
      />

      <AnimatedTextInput
        label={"Office Phone"}
        value={formState.officePhone}
        onChangeText={(text: string) => {
          const numericText = text.replace(/[^0-9]/g, "");
          updateFormState("officePhone", numericText);
        }}
        keyboardType="numeric"
      />

      {formState.officePhone.length !== 10 && (
        <View style={{ marginBottom: 14 }}>
          <Text style={styles.errorText}>
            <Icon name="cross" size={16} color="black" /> Enter a valid 10-digit
            phone number
          </Text>
        </View>
      )}

      <AnimatedTextInput
        label={"Brokerage Address"}
        value={formState.brokerageAddress}
        onChangeText={(value: string) =>
          updateFormState("brokerageAddress", value)
        }
      />

      <View style={styles.row}>
        <View style={styles.inputGroupHalf}>
          <AnimatedTextInput
            label={"Country"}
            value={formState.country}
            onChangeText={(value: string) => updateFormState("country", value)}
          />
        </View>

        <View style={styles.inputGroupHalf}>
          <AnimatedTextInput
            label={"Province"}
            value={formState.province}
            onChangeText={(value: string) => updateFormState("province", value)}
          />
        </View>
      </View>

      <View style={styles.row}>
        <View style={styles.inputGroupHalf}>
          <AnimatedTextInput
            label={"City"}
            value={formState.city}
            onChangeText={(value: string) => updateFormState("city", value)}
          />
        </View>

        <View style={styles.inputGroupHalf}>
          <AnimatedTextInput
            label={"Postal Code"}
            value={formState.postalCode}
            onChangeText={(text: string) => {
              const numericText = text.replace(/[^0-9]/g, "");
              updateFormState("postalCode", numericText);
            }}
            keyboardType="numeric"
          />
        </View>
      </View>
      {formState.postalCode.length !== 6 && formState.postalCode.length > 0 && (
        <View style={{ marginBottom: 14 }}>
          <Text style={styles.errorText}>
            <Icon name="cross" size={16} color="black" /> Postal code must be 6
            digits long
          </Text>
        </View>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  sectionTitle: {
    marginTop: 20,
    fontSize: 24,
    fontWeight: "700",
    lineHeight: 30,
    fontFamily: fonts.calibri.bold,
    marginBottom: 16,
    color: "#333",
  },
  inputGroupHalf: {
    position: "relative",
    flex: 1,
    marginHorizontal: 5,
  },
  row: {
    flexDirection: "row",
    justifyContent: "space-between",
  },
  errorText: {
    color: "black",
    marginTop: 4,
    fontSize: 14,
  },
});

export default BrokerageDetails;
