import { useEffect } from "react";
import { NavigationContainer } from "@react-navigation/native";
import * as SplashScreen from "expo-splash-screen";
import { useAppSlice, useAppService, IUser } from "@/modules/app";
import { loadImages, loadFonts } from "@/theme";
import { useDataPersist, DataPersistKeys } from "@/hooks";
import { isWeb } from "@/utils/deviceInfo";
import { createNativeStackNavigator } from "@react-navigation/native-stack";
import { AuthStackNavigator, HomeStackNavigator } from "./stack";
import { useSelector } from "react-redux";
import { RootState } from "@/redux/store";

// keep the splash screen visible while complete fetching resources
SplashScreen.preventAutoHideAsync();

function Navigator() {
  const { getUser } = useAppService();
  const { dispatch, setUser, setLoggedIn } = useAppSlice();
  const { setPersistData, getPersistData } = useDataPersist();
  const { loggedIn, user, isCheckedL } = useSelector(
    (state: RootState) => state.registration
  );

  const preload = async () => {
    try {
      await Promise.all([loadImages(), loadFonts()]);

      const user = await getUser();

      dispatch(setUser(user));
      dispatch(setLoggedIn(!!user));

      if (user) setPersistData<IUser>(DataPersistKeys.USER, user);

      SplashScreen.hideAsync();
    } catch (err) {
      console.log("[##] preload error:", err);

      getPersistData<IUser>(DataPersistKeys.USER)
        .then((user) => {
          if (user) {
            dispatch(setUser(user));
            dispatch(setLoggedIn(!!user));
          }
        })
        .finally(() => {
          SplashScreen.hideAsync();
        });
    }
  };

  useEffect(() => {
    preload();
  }, []);

  // TODO: switch router by loggedIn status
  console.log("[##] loggedIn", loggedIn, isCheckedL);

  return (
    <NavigationContainer>
      {isCheckedL && loggedIn ? <HomeStackNavigator /> : <AuthStackNavigator />}
    </NavigationContainer>
  );
}

export default Navigator;
